import React from "react";
import { AlertContext } from "./alertContext";
import {alertReducer} from './alertReducer'
import {HIDE_ALERT, SHOW_ALERT} from '../'

export const AlertState = ({children}) => {

    return (
        <AlertContext.Provider>
            {children}
        </AlertContext.Provider>
    )
}
